def hsv_to_rgb(h, s, v, a) -> tuple:
    """Convert HSV to RGB."""

    a = int(255*a)
    if s:
        if h == 1.0:
            h = 0.0
        i = int(h*6.0)
        f = h*6.0 - i

        w = int(255*(v * (1.0 - s)))
        q = int(255*(v * (1.0 - s * f)))
        t = int(255*(v * (1.0 - s * (1.0 - f))))
        v = int(255*v)

        if i == 0:
            return (v, t, w, a)
        if i == 1:
            return (q, v, w, a)
        if i == 2:
            return (w, v, t, a)
        if i == 3:
            return (w, q, v, a)
        if i == 4:
            return (t, w, v, a)
        if i == 5:
            return (v, w, q, a)
    else:
        v = int(255*v)
        return (v, v, v, a)


def temperature_color(temperature):
    """Map the temperature."""

    hue = 30 + 240 * (30 - temperature) / 60
    rgb = hsv_to_rgb(hue / 360.0, 1.0, 1.0, 1.0)
    return f'#{rgb[0]:02x}{rgb[1]:02x}{rgb[2]:02x}'


